import { React, useState, useRef } from "react";
import "./Menu.css";
import { Slant as Hamburger } from 'hamburger-react'

const Menu: React.FC = () => {
    const menuSize = 35;

    const dropDownRef = useRef(null);
    const [isOpen, setOpen] = useState(false)
    const handleToggle = () => {
        setOpen(!isOpen)
    }    

    return (
        <div className="menu_wrapper">
            <Hamburger className="menu_trigger" size={menuSize} distance="sm" onClick={handleToggle} rounded>
                <nav ref={dropDownRef} className={`menu ${isOpen ? 'open' : 'notOpen'}`}>
                    <ul>
                        <li><a href="/">Projects</a></li>
                        <li><a href="#contact">Contact</a></li>
                    </ul>
                </nav>
            </Hamburger>
        </div>
    );
};

export default Menu;
